import React, { useEffect } from "react";
import { Button, Row, Col, ListGroup, Image, Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Message from "../components/message";
import { getOrderDetails,payOrder } from "../actions/orderActions";
import Loader from "../components/loader";

function OrderScreen({ match }) {
  const orderId = match.params.id;
  console.log("id", orderId);
  const dispatch = useDispatch();
  const[sdkready,setSdkready] = useState(false)

  // name given in the store
  const orderDetails = useSelector((state) => state.orderDetails);
  const { order, error, loading } = orderDetails;

  const orderPay = useSelector((state) => state.orderPay);
  const { loading:loadingPay,success:successPay } = orderPay;

  if (!loading && !error) {
    order.itemsPrice = order.orderItems
      .reduce((acc, item) => acc + item.price * item.qty, 0)
      .toFixed(2);

    console.log("order", order);
  }
  //AQVKjliD0Pjeb16HbHLGs2KaGoRlg5D3Nsae3aIAZLuRsRTVl9PGtnKUr-5j-26Gw0zIND7YntGC0e_B

  const addpayPalScript = ()=>{
    const script = document.createElement('script')
    script.type='text/javascript'
    script.src= "https://www.paypal.com/sdk/js?client-id=AQVKjliD0Pjeb16HbHLGs2KaGoRlg5D3Nsae3aIAZLuRsRTVl9PGtnKUr-5j-26Gw0zIND7YntGC0e_B"
    script.async = true
    script.onload = () =>{
      setSdkready(true)
    }
    document.body.appendChild(script)
  }

  const successPaymentHandler = (paymentResult)=>{
    dispatch(payOrder(orderId,paymentResult))
  }

  useEffect(() => {
    if (!order ||successPay || order._id !== Number(orderId)) {
      dispatch(getOrderDetails(orderId));
    }
    else if(!order.isPaid){
      if(!window.paypal){

        addpayPalScript()
      }
      else{
        setSdkready(true)
      }
    }
  }, [dispatch,order, orderId]);
  return loading ? (
    <Loader />
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <div>
      <h1>Order : {order._id}</h1>
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Shipping</h2>
              <p>
                <strong>Name:</strong>
                {order.user.name}
              </p>
              <p>
                <strong>Email:</strong>
                <a href={`mailto: ${order.user.email}`}>{order.user.email}</a>
              </p>
              <p>
                <strong>Shipping:</strong>
                {order.shippingAddress.address},{order.shippingAddress.city}{" "}
                {order.shippingAddress.postalCode},{" "}
                {order.shippingAddress.country},
              </p>

              {order.isDelivered ? (
                <Message variant="success">Delivered on : {order.deliveredAt}</Message>
              ) : (
                <Message variant="warning">Not Delivered </Message>
              )}
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Payment Method</h2>
              <p>
                <strong>Method:</strong>
                {order.paymentMethod}
              </p>
              {order.isPaid ? (
                <Message variant="success">Paid on : {order.paidAt}</Message>
              ) : (
                <Message variant="warning">NotPaid</Message>
              )}
            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Order Items</h2>

              {order.orderItems.length === 0 ? (
                <Message variant="info">Your order is empty</Message>
              ) : (
                <ListGroup variant="flush">
                  {order.orderItems.map((item, index) => (
                    <ListGroup.Item key={index}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.image}
                            alt={item.name}
                            fluid
                            rounded
                          />
                        </Col>

                        <Col>
                          <Link to={`/products/${item.product}`}>
                            {item.name}
                          </Link>
                        </Col>
                        <Col md={4}>
                          {item.qty} X ${item.price} = $
                          {(item.qty * item.price).toFixed(2)}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Order Summary</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Item:</Col>
                  <Col>${order.itemsPrice}</Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Row>
                  <Col>Shipping:</Col>
                  <Col>${order.shippingPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Tax:</Col>
                  <Col>${order.taxPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Total:</Col>
                  <Col>${order.totalPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                {error && <Message variant="danger">{error}</Message>}
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default OrderScreen;
