import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Button, Row, Col, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/loader";
import Message from "../components/message";
import FormContainer from "../components/FormContainer";
import { register } from "../actions/userActions";

function RegisterScreen({location,history}) {
  const [name, setName] = useState("");

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  const dispatch = useDispatch()
  const redirect = location.search ? location.serach.split('=')[1]:'/'
  console.log(redirect,"sowthri11111111111111111111111111");

  const userRegister = useSelector(state => state.userRegister) //this userRegister is comes from store.js
  const {error,loading,userInfo} = userRegister

  useEffect(()=>{
      if(userInfo){
          history.push(redirect)
      }
  },[history,userInfo,redirect])



  const submitHandler = (e) => {
    e.preventDefault();
    if(password != confirmPassword ){
        setMessage('password do not match')
    }
    else{
        dispatch(register(name,email,password))
        console.log("submitted register");
    }
    
  };
  return (
    <FormContainer>
      <h1>Sign Up</h1>
      {message && <Message variant = 'danger'>{message}</Message>}
      {error && <Message variant = 'danger'></Message>}
      {loading && <Loader/>}
      <Form onSubmit={submitHandler}>
      <Form.Group controlId="name">
          <Form.Label>Name</Form.Label>
          <Form.Control
          required
            type="name"
            placeholder="enter a name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          ></Form.Control>
          </Form.Group>
        <Form.Group controlId="email">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
          required
            type="email"
            placeholder="enter a email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="password">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
          required
            type="password"
            placeholder="enter a password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Form.Group controlId="confirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
          required
            type="password"
            placeholder="enter a confirmPassword"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          ></Form.Control>
        </Form.Group>

        <Button type="submit" variant="primary">
      Register
        </Button>

        <Row className="py-3">
          <Col>
           Have an Account ?
            <Link
              to={redirect ? `/loginr?redirect=${redirect}` : "/login"}
            >
              Login
            </Link>
          </Col>
        </Row>
      </Form>
    </FormContainer>
  );
}

export default RegisterScreen;
